import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate for React Router v6
import "./InsideProjectPage.css";
import { BsEmojiSmile, BsArrowRight } from "react-icons/bs";
import { BsPaperclip } from "react-icons/bs";
import { BiMicrophone } from "react-icons/bi";

const InsideProjectPage = () => {
  const [inputText, setInputText] = useState("");
  const navigate = useNavigate(); // Use useNavigate for React Router v6
  const componentRef = useRef(null);

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleBackButtonClick = () => {
    console.log("Back button clicked");
    navigate(-1); // Go back to the previous page using useNavigate
  };

  // Close the component if clicked outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (componentRef.current && !componentRef.current.contains(e.target)) {
        console.log("Clicked outside. Closing component.");
        navigate(-1);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [componentRef, navigate]);

  return (
    <div className="main-card" ref={componentRef}>
      <button className="back-button" onClick={handleBackButtonClick}>
        Back
      </button>
      <div className="small-card-upper">
        <p>Initial sketching</p>
        <BsArrowRight />
        <h1>3d model</h1>
        <BsArrowRight />
        <p>product Designing</p>
        <BsArrowRight />
        <p>Installation</p>
      </div>
      {/* ... rest of the code ... */}
    </div>
  );
};

export default InsideProjectPage;
